import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import "./style.css";

class Login extends Component {
  constructor(props) {
    super(props)

    this.state = {
        username: '',
        password: '',
    }
  }

  updateInput = (event) => {
    event.preventDefault();
    this.setState({
        [event.target.name]: event.target.value
    });
  }

  login = (event) => {
    event.preventDefault();
    const body =  JSON.parse ({
        username: this.state.username,
        password: this.state.password
    });
    fetch('/api/login', {
        method: "POST",
        body: body,
        headers: {
            "Content-Type": "application/json"
        }
    });
  }

  render() {
    return (
      <div className="login-wrapper">
        <form className="login-form">
            <label className="unsername-label">Username: </label><br/>
            <input className="unsername-input" type="text" name="unsername" value={this.state.value} placeholder="Enter username" onChange={this.updateInput}/><br/>
            <label className="email-label">Password: </label><br/>
            <input className="unsername-input" type="password" name="password" value={this.state.value} placeholder="Enter password" onChange={this.updateInput}/><br/>
            <button className="login-button" onClick={this.login}>Login</button>
        </form> 
      </div>
    )
  }
}

export default Login;
