import React, { Component } from 'react';
import VolunteeringOpportunitiesMap from '../VoluneeringOpportunitiesMap';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import './style.css';

class VolunteeringOpportunityDetails extends Component {

    constructor(props) {
      super(props)

      this.state = {
         volunteeringOpportunity: {}
      }
    }

    componentDidMount = async() => {
        this.fetchVolunteeringOpportunity();
    }

    fetchVolunteeringOpportunity = async() => {
        const id = this.props.match.params.id;
        const volunteeringOpportunity = await (await fetch(`/api/opportunities/${id}`, {
            headers: {
                'jwt-token': localStorage.getItem('user-jwt')
            }
        })).json();
        this.setState({
            volunteeringOpportunity: volunteeringOpportunity
        });
    }

  render() {
    const { title, organization, organizationURL, description, address, duration } = this.state.volunteeringOpportunity;
        return (
            <div >
                <div className="volunteering-opportunity-details-wrapper">
                    <div className="volunteering-opportunity-map-wrapper">
                        {this.state.volunteeringOpportunity.latitude && (
                            <VolunteeringOpportunitiesMap
                                className="volunteering-opportunity-map"
                                id={this.props.match.params.id}
                                volunteeringOpportunity={this.state.volunteeringOpportunity}
                            />
                        )}

                    </div>
                    <div className="volunteer-experience-details">
                        <button className="experience-delete-button" onClick={this.deleteExperience}><Link className="back-link" to="/volunteer">Delete</Link></button>
                        <button className="back-button"><Link className="back-link" to="/volunteer">Go Back</Link></button>
                        <h2 className="volunteering-opportunity-title">{title}</h2>
                        <h3 className="volunteer-experience-organization">{organization}</h3>
                        <p className="volunteer-experience-organization-website"><a className="volunteer-experience-organization-website-link" href={organizationURL} target="/blank">{organizationURL}</a></p>
                        <p className="volunteer-experience-description-title">Description:</p>
                        <p className="volunteer-experience-description-text">{description}</p>
                        <p className="volunteer-experience-address-title">Address:</p>
                        <p className="volunteer-experience-address-text">{address}</p>
                        <p className="volunteer-experience-duration-title">Duration:</p>
                        <p className="volunteer-experience-duration-text">{duration}</p>

                    </div>
                </div>
            </div>
        )
    }
}

export default VolunteeringOpportunityDetails;
