import React, { Component } from 'react';
import VolunteerExperience from '../VolunteerExperience';
import './style.css';

class VolunteerExperienceList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            experiences: []
        }
    }

    componentDidMount = async () => {
        this.getVolunteerExperiences();
    }

    getVolunteerExperiences = async () => {
        const experiences = await (await fetch('/api/volunteer/experiences', {
            headers: {
                'jwt-token': localStorage.getItem('user-jwt')
            }
        })).json();
        this.setState({
            experiences: experiences
        });
    }

    getUser = async () => {
        const user = await (await fetch('/api/volunteer', {
            method: "GET",
            headers: {
                "jwt-token": localStorage.getItem('user-jwt')
            }
        })).json();
        this.setState({
            user: user
        });
    }

    deleteExperience = async (event) => {
        const ExperienceToDelete = await fetch('/api/volunteer/experiences', {
            method: "DELETE",
            body: JSON.stringify({ experienceId: event.target.value }),
            headers: {
                'jwt-token': localStorage.getItem('user-jwt')
            }
        });
        this.getUser();
        this.getVolunteerExperiences();
    }

    render() {
        return (
            <div className="volunteer-experiences">
                <h2 className="volunteer-experiences-title">My Volunteering Experiences</h2>
                {this.state.experiences.length === 0 && (
                    <p className="no-message">No experiences recorded!</p>
                )}
                <div className="volunteer-experiences-wrapper">
                    {this.state.experiences.length > 0 && this.state.experiences.map(experience => {
                        let count = 0;
                        return (
                            <VolunteerExperience
                                key={experience.id-`${count}`}
                                id={experience.id}
                                title = {experience.title}
                                deleteExperience = {this.deleteExperience}
                            />
                        )
                    })}
                </div>
            </div>
        )
    }
}

export default VolunteerExperienceList;
