import React, { Component } from 'react';
import './style.css';
import SearchNav from '../SearchNav';

class Volunteer extends Component {
    constructor(props) {
        super(props)

        this.state = {
            volunteer: {}
        }
    }

    componentDidMount = async() => {
        this.getVolunteer();
    }

    getVolunteer = async() => {
        const volunteer = await(await fetch('/api/volunteer',{
            method: "GET",
            headers:{
                "jwt-token":localStorage.getItem('user-jwt') 
            }
        })).json();
        this.setState({
            volunteer: volunteer
        });
    }

    render() {
        const profilePictureSrc = this.state.volunteer.profilePicture ? this.state.volunteer.profilePicture : "./images/portrait_placeholder.png";
        return (
            <div>
                <SearchNav />
                <div className="volunteer-wrapper">
                    <div className="volunteer-info-wrapper">
                        <h2>Welcome {this.state.volunteer.fullName}!</h2>
                            <div className="volunteer-picture-wrapper">
                                <img src={profilePictureSrc} alt="volunteer"/>
                            </div>
                        <p>{this.state.volunteer.username}</p>
                        <p>Your score is {this.state.volunteer.score}</p>
                        <p>{this.state.volunteer.address}</p>
                        <p>{this.state.volunteer.email}</p>
                        <button className="update-button">Update Your Profile</button>
                    </div>
                    {/* <VolunteerExperience /> */}
                </div>
            </div>
        )
    }
}

export default Volunteer;
