import React, { Component } from 'react';
import './style.css';
import SearchNav from '../SearchNav';
import { BrowserRouter as Router, Link } from 'react-router-dom';

class VolunteeringOpportunityList extends Component {

    constructor(props) {
        super(props)

        this.state = {
            volunteeringOpportunities: [],
        }
    }

    componentDidMount = async () => {
        let url = '/api/opportunities';
        let zipcode = this.props.match.params.zipcode;
        if (zipcode) {
            url += `?zipcode=${zipcode}`;
        }
        const volunteeringOpportunities = await (await fetch(url)).json();
        this.setState({
            volunteeringOpportunities: volunteeringOpportunities
        });
    }

    render() {
        return (
            <div>
                <SearchNav />
                <div className="volunteer-opportunities">
                    <h2 className="volunteer-opportunities-title">Your search results</h2>
                    <h3 className="volunteer-opportunities-subtitle">Volunteering opportunities in zipcode {this.props.match.params.zipcode}:</h3>
                    {this.state.comingExperiences.length === 0 && (
                        <p className="no-message">No coming experiences recorded!</p>
                    )}
                    <ul className="volunteer-coming-experiences-list">
                        {this.state.comingExperiences.length > 0 && this.state.comingExperiences.map(comingExperience => {
                            let count = 0;
                            return (
                                <div>
                                    <VolunteerComingExperience
                                        key={comingExperience.id - `${count += 1}`}
                                        id={comingExperience.id}
                                        title={comingExperience.title}
                                        rating={this.state.pastExperienceRating}
                                        comment={this.state.pastExperienceComment}
                                        updateRating={this.updateRating}
                                        updateComment={this.updateComment}
                                        deleteExperience={() => this.deleteExperience(comingExperience.id)}
                                        completedExperience={() => this.completedExperience(comingExperience.id)}
                                    />
                                </div>
                            )
                        })}
                    </ul>
                </div>
            </div>
        )
    }
}

export default VolunteeringOpportunityList;
