import React, { Component } from "react";
import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import "./style.css";

class App extends Component {
  constructor(props) {
    super(props)
    const token = localStorage.getItem('user-jwt');
    this.state = {
       isLoggedIn: false || token,
       zipcode:"",
    }
  }

  render() {
    return (
      <Router>
        <div className='app'>
          <header>
            <h1>Give Back NYC</h1>
            <h2>to your community</h2>
          </header>
          {this.isLoggedIn && (
            <form>
              <label>Search volunteering opportunities by zipcode: 
                <input type="text" placeholder="Enter Zipcode" value={this.state.zipcode} onChange={this.handleChange}/>
              </label>
              <button onClick={this.zipcodeSearch}>Go!</button>
              <label>Search volunteering opportunities by zipcode: 
                <button onClick={this.nearYouSearch}>Go!</button>
              </label>
            </form>
          )}
          <div class="body"></div>  
            <PrivateRoute exact path="/albums" component={AlbumList} />
              <PrivateRoute exact path='/albums/:id' component={AlbumDetails} />
              <PrivateRoute exact path='/my-collection' component={UserCollection} />
              <PrivateRoute exact path='/users/:id' component={BrowseUserMate} />
              <PrivateRoute exact path='/my-collection/update' component={UpdateUser} />
              <Route exact path='/' component={Login} />
              <Route exact path='/register' component={Register} />
        </div>
    </Router>
    )
  }
}

export default App;
