import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import VolunteeringOpportunityList from '../VolunteeringOpportunityList';
import "./style.css";

class SearchNav extends Component {
    constructor(props) {
        super(props)

        this.state = {
            zipcode: "",
            volunteeringOpportunities:[]
        }
    }

    component

    handleClick = async() => {
        let url = '/api/opportunities';
        let zipcode = this.state.zipcode;
        if (zipcode) {
            url += `?zipcode=${zipcode}`;
        }
        const volunteeringOpportunities = await (await fetch(url)).json();
        this.setState({
            volunteeringOpportunities: volunteeringOpportunities
        });
    }

    handleChange = (event) => {
        event.preventDefault();
        this.setState({
            zipcode: event.target.value
        });
    }

    render() {
        return (
            <nav>
                <form className="search-form" onClick={this.handleClick}>
                    <div className="search-zipcode-wrapper">
                        <label className="search-zipcode-label">Search volunteering opportunities by zipcode: </label>
                        <div className="search-zipcode">
                            <input className="search-zipcode-input" type="text" placeholder="Enter Zipcode" value={this.state.zipcode} onChange={this.handleChange} />
                            <button className="zipcode-button" OnClick={this.handleClick}><Link className="zipcode-button-link" to={`/opportunities/${this.state.zipcode}`}>Go</Link></button><br />
                            <Route path={`/opportunities/${this.state.zipcode}`} exact render={(props) => <VolunteeringOpportunityList {...props} />} />
                        </div>
                    </div>
                    <div className="search-nearyou-wrapper">
                        <label className="search-nearyou-label">Or near you: </label>
                        <button className="go-button" onClick={this.nearYouSearch}>Go!</button>
                    </div>
                </form>
                <button className="logout-button" onClick={this.props.logOut} ><Link className="logout-link" to='/' >Log Out</Link></button>
            </nav>
        )
    }
}
export default SearchNav;
