import React, { Component } from 'react';
import './style.css';

class VolunteerExperienceList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            comingExperiences: [],
            pastExperiences: []
        }
    }

    componentDidMount = async () => {
        const comingExperiences = await (await fetch('/api/volunteer/experiences', {
            headers: {
                'jwt-token': localStorage.getItem('user-jwt')
            }
        })).json();
        this.setState({
            comingExperiences: comingExperiences
        });
    }

    render() {
        return (
            <div className="volunteer-experiences">
                <h2 className="volunteer-experiences-title">My Volunteering Experiences</h2>
                <h3>Coming experiences:</h3>
                <ul className="volunteer-coming-experiences-list">
                    {this.state.comingExperiences.length === 0 && (
                        <p>No coming experiences recorded!</p>
                    )}
                    {this.state.comingExperiences.length > 0 && this.state.comingExperiences.map(comingExperience => {
                        return (
                            <div className="volunterr-coming-experience-wrapper">
                                <li className="volunteer-experience-item" key={comingExperience.id}>{comingExperience.title}</li>
                                <button className="delete-button" onClick={this.deleteExperince}>Delete</button>
                                <button className="completed-button">Completed</button>
                            </div>
                        )
                    }

                    )}
                </ul>
                <h3>Past Experiences:</h3>
                <ul className="volunteer-past-experiences-list">
                    {this.state.pastExperiences.length === 0 && (
                        <p>No past experiences recorded!</p>
                    )}
                </ul>
            </div>
        )
    }
}

export default VolunteerExperienceList;
