import React, { Component } from 'react';
import './style.css';

class VolunteerPastExperience extends Component {
  constructor(props) {
    super(props)

    this.state = {
      pastExperienceRating: undefined,
      pastExperienceComment: ''
    }
  }

  componentDidMount = async () => {
    this.getVolunteerPastExperience();
  }

  getVolunteerPastExperience = async () => {
    const pastExperience = await (await fetch(`/api/volunteer/experiences/past/${this.props.id}`, {
      headers: {
        'Content-Type': 'application/json',
        'jwt-token': localStorage.getItem('user-jwt')
      }
    })).json();
    this.setState({
      pastExperienceRating: pastExperience.rating,
      pastExperienceComment: pastExperience.comment ? pastExperience.comment : this.state.pastExperienceComment
    });
  }

  getStarRating = () => {
    let starRating='';
    for(let i=0; i< this.state.pastExperienceRating; i++){
        starRating+='⭐️'
    }
    return starRating;
}

  render() {
    return (
      <div className="volunteer-past-experience-wrapper">
        <li className="volunteer-experience-item">{this.props.title}</li>
        <span>{this.getStarRating()}</span>
        <button className="delete-button" onClick={this.props.deleteExperience}>Delete</button>
      </div>
    )
  }
}
export default VolunteerPastExperience;
