import React, { Component } from 'react';
import './style.css';

class VolunteerComingExperience extends Component {
  constructor(props) {
    super(props)

    this.state = {
       isButtonClicked: false,
       rating:'',
       comment:''
    }
  }

  handleClick = () => {
    this.setState({
      isButtonClicked: true
    })
  }

  handleChange = event => {
    event.preventDefault();
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  submitRating = event => {
    event.preventDefault();
    this.props.completedExperience(event);
    this.setState({
      isButtonClicked: false,
       rating:'',
       comment:''
    })
  }

  render() {
    return (
      <div className="volunteer-coming-experience-wrapper">
        <li className="volunteer-experience-item">{this.props.title}</li>
        <button className="delete-button" onClick={this.props.deleteExperience}>Delete</button>
        <button className="completed-button" onClick={this.handleClick}>Completed</button>
        {this.state.isButtonClicked && 
          <form className="volunteer-experience-rating-form">
            <label className="rating-label">Rate your experience (out of 5)</label><br/>
            <input type="text" placeholder="Rating" name="rating" value={this.state.rating} onChange={this.handleChange}/><br/>
            <label className="rating-label">Leave a comment:</label><br/>
            <textarea name="comment" value={this.state.comment} onChange={this.handleChange}></textarea><br/>
            <button onClick={this.submitRating}>Submit</button>
          </form>
        }

      </div>
    )
  }
}
export default VolunteerComingExperience;
