import React, { Component } from 'react';
import './style.css';
// import VolunteeringOpportunityDetails from '../VolunteeringOpportunityDetails';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';

class VolunteeringOpportunityList extends Component {

    // constructor(props) {
    //     super(props)

    //     this.state = {
    //         volunteeringOpportunities: [],
    //     }
    // }

    // componentDidMount = async () => {
    //     let url = '/api/opportunities';
    //     let zipcode = this.props.match.params.zipcode;
    //     if (zipcode) {
    //         url += `?zipcode=${zipcode}`;
    //     }
    //     const volunteeringOpportunities = await (await fetch(url)).json();
    //     this.setState({
    //         volunteeringOpportunities: volunteeringOpportunities
    //     });
    // }

    render() {
        return (
            <div>
                {this.props.volunteeringOpportunities && (
                    <div  className="volunteering-opportunities">
                        <h2 className="volunteering-opportunities-title">Your search results</h2>
                        <h3 className="volunteering-opportunities-subtitle">Volunteering opportunities in zipcode {this.props.match.params.zipcode}:</h3>
                        {this.props.volunteeringOpportunities.length === 0 && (
                    <p className="no-message">Sorry, there are no volunteering opportunities in this area at the moment!</p>
                )}
                        <div className="volunteering-opportunities-list">
                            {this.props.volunteeringOpportunities.length > 0 && this.props.volunteeringOpportunities.map(volunteeringOpportunity => {
                        let count = 0;
                        return (
                            <div>
                                <p className="volunteering-opportunity-title" key={volunteeringOpportunity.id - `${count += 1}`}>{volunteeringOpportunity.title}</p>
                                <p className="volunteering-opportunity-organization">{volunteeringOpportunity.organization}</p>
                                <Route path={`/opportunities/${volunteeringOpportunity.id}`} exact component={VolunteeringOpportunityDetails} />
                            <button className="add-button" onClick={this.addOpportunity}>Add volunteering opportunity</button>
                            </div>
                        )
                    })}
                        </div>
                    </div>
                )}

            </div>
        )
    }
}

export default VolunteeringOpportunityList;
