import React, { Component } from 'react';
import './style.css';

class VolunteerExperienceList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            comingExperiences: [],
            pastExperiences: []
        }
    }

    componentDidMount = async () => {
        this.getVolunteerExperiences();
    }

    getVolunteerExperiences = async() => {
        const comingExperiences = await (await fetch('/api/volunteer/experiences', {
            headers: {
                'jwt-token': localStorage.getItem('user-jwt')
            }
        })).json();
        this.setState({
            comingExperiences: comingExperiences
        });
    }

    getUser = async () => {
        const user = await (await fetch('/api/volunteer', {
            method: "GET",
            headers: {
                "jwt-token": localStorage.getItem('user-jwt')
            }
        })).json();
        this.setState({
            volunteer: user
        });
    }

    deleteExperience = async(id) => {
        const ExperienceToDelete = await fetch('/api/volunteer/experiences', {
            method:"DELETE",
            body: { experienceId: id },
            headers: {
                'jwt-token': localStorage.getItem('user-jwt')
            }
        });
        this.getVolunteer();
        this.getVolunteerExperiences();
    }

    render() {
        return (
            <div className="volunteer-experiences">
                <h2 className="volunteer-experiences-title">My Volunteering Experiences</h2>
                <h3 className="volunteer-experiences-subtitle">Coming experiences:</h3>
                <ul className="volunteer-coming-experiences-list">
                    {this.state.comingExperiences.length === 0 && (
                        <p className="no-message">No coming experiences recorded!</p>
                    )}
                    {this.state.comingExperiences.length > 0 && this.state.comingExperiences.map(comingExperience => {
                        let count=0;
                        return (
                            <div className="volunteer-coming-experience-wrapper">
                                <li className="volunteer-experience-item" key={comingExperience.id-`${count+=1}`}>{comingExperience.title}</li>
                                <button className="delete-button" onClick={this.deleteExperience}>Delete</button>
                                <button className="completed-button" onClick={this.completedExperience}>Completed</button>
                            </div>
                        )
                    })}
                </ul>
                <h3 className="volunteer-experiences-subtitle">Past Experiences:</h3>
                <ul className="volunteer-past-experiences-list">
                    {this.state.pastExperiences.length === 0 && (
                        <p className="no-message">No past experiences recorded!</p>
                    )}
                </ul>
            </div>
        )
    }
}

export default VolunteerExperienceList;
