import React, { Component } from 'react';
import ReactMapGL, { Marker } from 'react-map-gl';
import Pin from './volunteer-opportunity-pin';
import './style.css';

const MAPBOX_TOKEN = "pk.eyJ1IjoiYW1ob3VlbCIsImEiOiJjamxzYmY1cDIwMGJwM3BwaDJjenQwbW80In0.GiTCZZwyjGsxC_LIsJtX5g";

class VolunteerExperienceDetailsMap extends Component {

    constructor(props) {
        super(props)

        this.state = {
            volunteerOpportunity: {},
            viewport: {
                width: window.innerWidth,
                height: window.innerHeight,
                latitude: 40.7410986,
                longitude: -73.997623,
                zoom: 12
            }
        }
    }

    componentDidMount = async() => {
        const id = this.props.id;
        const volunteerOpportunity = await (await fetch(`/api/volunteer/opportunities/${id}`, {
            headers: {
                'jwt-token': localStorage.getItem('user-jwt')
            }
        })).json();
        this.setState({
            volunteerOpportunity: volunteerOpportunity
        });
    }

    _renderMarker() {
        const latitude = this.state.volunteerOpportunity.latitude;
        const longitude = this.state.volunteerOpportunity.longitude;
        const title = this.state.volunteerOpportunity.title;

        return (
            <Marker longitude={longitude} latitude={latitude} >
                <div className="volunteer-opportunity">{title}</div>
                <Pin size={20}/>
            </Marker>
        );
    }

    render() {

        const { viewport } = this.state;

        return (
            <div>
                <p>{this.state.volunteerOpportunity.latitude}</p>
                {this.state.volunteerOpportunity.longitude}
            <ReactMapGL
                {...viewport}
                mapStyle="mapbox://styles/mapbox/light-v9"
                onViewportChange={(viewport) => this.setState({ viewport })}
                mapboxApiAccessToken={MAPBOX_TOKEN}
            >
                {this._renderMarker}
            </ReactMapGL>
            </div>
        );
    }

}

export default VolunteerExperienceDetailsMap;
