import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import "./style.css";
import Login from '../Login';

class App extends Component {
  constructor(props) {
    super(props)
    const token = localStorage.getItem('user-jwt');
    this.state = {
      isLoggedIn: false || token,
      zipcode: "",
    }
  }

  handleChange = (event) => {
    event.preventDefault();
    this.setState({
      zipcode: event.target.value
    });
  }

  logOut = () => {
    localStorage.clear();
  }

  render() {
    return (
      <Router>
        <div className='app'>
          <header>
            <h1 className="header-title">Give Back NYC</h1>
            <h2 className="header-subtitle">to your community</h2>
          </header>
          {this.isLoggedIn && (
            <nav>
              <form className="search-form" onSubmit={this.zipcodeSearch}>
                <div className="search-zipcode-wrapper">
                  <label className="search-zipcode-label">Search volunteering opportunities by zipcode: </label>
                  <div className="search-zipcode">
                    <input className="search-zipcode-input" type="text" placeholder="Enter Zipcode" value={this.state.zipcode} onChange={this.handleChange} />
                    <button className="zipcode-button" onClick={this.zipcodeSearch}>Go!</button><br />
                  </div>
                </div>
                <div className="search-nearyou-wrapper">
                  <label className="search-nearyou-label">Or near you: </label>
                  <button className="go-button" onClick={this.nearYouSearch}>Go!</button>
                </div>
              </form>
              <button className="logout-button" onClick={this.logOut} ><Link className="logout-link" to='/' >Log Out</Link></button>
            </nav>
          )}
          {!this.isLoggedIn && (
          <div className="route-wrapper">
            <Route  path='/' exact component={Login} />
            <Route path='/register' exact component={Register} /> 
          </div>
          )}
        </div>
      </Router>
    )
  }
}

export default App;
