import React, { Component } from 'react';
import ReactMapGL, { Marker } from 'react-map-gl';
// import WebMercatorViewport from 'viewport-mercator-project';
import './style.css';

const MAPBOX_TOKEN = "pk.eyJ1IjoiYW1ob3VlbCIsImEiOiJjamxzYmY1cDIwMGJwM3BwaDJjenQwbW80In0.GiTCZZwyjGsxC_LIsJtX5g";
const viewport =  WebMercatorViewport({
    width: window.innerWidth,
    height: window.innerHeight,
    latitude: 40.7410986,
    longitude: -73.997623,
    zoom: 12
});

class VolunteerExperienceDetailsMap extends Component {

    constructor(props) {
        super(props)

        this.state = {
            volunteerOpportunity: {},
            viewport: viewport
        }
    }

    componentDidMount = async() => {
        const id = this.props.match.params.id;
        const volunteerOpportunity = await (await fetch(`/api/volunteer/opportunities/${id}`, {
            headers: {
                'jwt-token': localStorage.getItem('user-jwt')
            }
        })).json();
        this.setState({
            volunteerOpportunity: volunteerOpportunity
        });
    }

    _renderMarker(volunteerOpportunity) {
        const lat = volunteerOpportunity.latitude;
        const lng = volunteerOpportunity.longitude;
        const name = volunteerOpportunity.title;

        return (
            <Marker longitude={lng} latitude={lat} >
                <div className="volunteer-opportunity">{name}</div>
            </Marker>
        );
    }

    render() {

        const { viewport, volunteerOpportunity } = this.state;

        return (
            <ReactMapGL
                {...viewport}
                mapStyle="mapbox://styles/mapbox/light-v9"
                onViewportChange={(viewport) => this.setState({ viewport })}
                mapboxApiAccessToken={MAPBOX_TOKEN}
            >
                {this._renderMarker(volunteerOpportunity)}
            </ReactMapGL>
        );
    }

}

export default VolunteerExperienceDetailsMap;
